                        )
                    }
                    case 'minus-qty':
                        return {
                            ...state,cartItems:state.cartItems.map((item)=>
                            item.id === action.payload.id ? ({...item,qty:item.qty-1}):item
                        )
                    }

                    case 'add-tottal-price':
