import React from "react";
import { useState } from "react";

function FilterdDishes(props) {
  console.log("single dish menu", props.singleDish);


  let [allMenus, setallMenus] = useState(props.allMenus);
  let [filterdDishes, setfilterdDishes] = useState(['']);
  let [active, setacttive] = useState("Beef");

  function showFilterdDishes(category) {
    props.setSingleDish([])
    setacttive(category);

    let filterdDishesAre = allMenus
      .filter((item) => {
        return item.strCategory === category;
      })
      .map((item) => {
        return (
          <li>
            <img src={item.strMealThumb} className="br" alt="" />
            <h5>{item.strMeal}</h5>
          </li>
        );
      });
    setfilterdDishes(filterdDishesAre);
  }


  let singleDish= props.singleDish.map((item)=>{
    return (
      <li>
        <img src={item.strMealThumb} className="br" alt="" />
        <h5>{item.strMeal}</h5>
      </li>
    );
  })




  let allCaegories = props.allMenuCategories.map((items) => {
    return (
      <li
        className={items.strCategory === active ? "active" : ""}
        onClick={() => {
          showFilterdDishes(items.strCategory);
        }}
      >
        {items.strCategory}
      </li>
    );
  });
  return (
    <div className="filterd-dishes">
      <div className="container">
        <div className="text-center categoy-dish">
          <h2>Choose your Dishes</h2>
          <p>
            Lorem ipsum dolor sit amet, consectetur adipisicing elit. Aliquam
            velit quia dolor explicabo illo excepturi dolorum architecto dolorem
            laborum voluptates?
          </p>
        </div>
      </div>
      <div className="filterd-dishes-content">
        <ul>{allCaegories}</ul>
      </div>
      <div className="filterd-dishes-result">
        <ul className="flex flex-wrap gap-25 ">
            {singleDish}
            {filterdDishes.length >0 ? filterdDishes : 
              <div className="alert">
                  <h3>No items Found.</h3>
                  <h4>Please Choose another Menu</h4>    
              </div>}
            </ul>
      </div>
    </div>
  );
}

export default FilterdDishes;
