import React from 'react'
import {useEffect,useState} from 'react'
import FilterdDishes from './FilterdDishes'
import Hero from './Hero'
import Loader from './Loader'
import SpecialDishesh from './SpecialDishesh'
import allMenus from './AllMenuContext'



export const AllMenuContext = React.createContext()

const Menus = () => {

  let [menu,setMenu]=useState([])
  let [menuCatagory,setMenuCatagory] = useState([])
  let [loading,setLoading] = useState(false)
  let [singleDish,setsingleDish] = useState([])


    async function getdata(){
        setLoading(true)
        const API_URL = "https://www.themealdb.com/api/json/v1/1/search.php?f=c"
        let responce = await fetch(API_URL)
        let data = await responce.json()
        setMenu(data.meals)
        setLoading(false)
    }

    async function getCatagoryData(){
      const CATEGORY_API_URL= "https://www.themealdb.com/api/json/v1/1/categories.php"
      let responce = await fetch(CATEGORY_API_URL)
      let categorydata = await responce.json()
      setMenuCatagory( categorydata.categories)
  }

    async function getOneDish(){
      const API_URL= "https://www.themealdb.com/api/json/v1/1/filter.php?c=Beef"
      let responce = await fetch(API_URL)
      let singleDishdata = await responce.json()
      setsingleDish(singleDishdata.meals)
  }


    useEffect( ()=>{
        getdata();
        getCatagoryData();
        getOneDish();
    },[])


  return (
    <div>
     <Hero />

    <AllMenuContext.Provider value={menu} >
        {!loading ? <SpecialDishesh /> : <Loader/>}
        {!loading ? 
         <FilterdDishes 
            allMenuCategories={menuCatagory} 

            singleDish={singleDish}  
            setSingleDish={setsingleDish}
          />: null}
     </AllMenuContext.Provider>
    </div>
  )
}

export default Menus
