import React from 'react'
import CardDish from './CardDish';
import {useState , useContext} from 'react'
import PopUp from './PopUp';
import AddtoCart from './AddtoCart'
import {AllMenuContext} from './AllMenuContext'

function SpecialDishesh(props) {

    let [showPopup,setshowPopup]= useState(false)
    let [popupItems,setpopupItems]= useState('')
    let [cartitems,setcartItem]= useState([{
                                name: '',
                                image: ''
                            }])

    const allmenuContext = useContext(AllMenuContext)

    function showPopupHandler(dishname){
        setpopupItems(dishname)
        setshowPopup(true)
    }
    function closepopupHandler(){
        setshowPopup(false)
    }

    function adToCartHandler(addTocartimg , addTocarttitle){
        console.log('add to cart now',addTocartimg,addTocarttitle);
        setcartItem({...cartitems,addTocarttitle,addTocartimg})
    }


    let specialDishes = 8;

    let specialMenus = allmenuContext.map((menuItem,index)=>{
        if(index<specialDishes){

            return(
                <CardDish menuitem = {menuItem} showPopupHandler={showPopupHandler} />
            )}
            return null
    })

  return (
    <section className='special-dishes section-bg'>
        {showPopup && 
            <PopUp 
                closepopupHandler={closepopupHandler}  
                popupItems={popupItems}
                adToCartHandler={adToCartHandler}
                >
            </PopUp>}
        <div className="container">
            <AddtoCart cartitems={cartitems}>

            </AddtoCart>
            <div className="special-dishes-content text-center">
                <h2> special Dishes</h2>  
                <p>Lorem ipsum dolor sit, amet consectetur adipisicing elit. Ratione quae, explicabo reiciendis distinctio animi similique, velit aspernatur quod commodi sequi quidem numquam eligendi quasi ipsam!</p>
            </div>
            <div className="special-dishes-list">
               <ul className="flex flex-wrap gap-25">
                    {specialMenus}
                </ul>
            </div>
        </div>
    </section>
  )
}

export default SpecialDishesh
