import React, { useContext, useState } from "react";
import { StateContext, DispatchContext } from "../../context/AppProvider";
import { useNavigate } from "react-router-dom";

function UseForm({ closeuserForm, itemid }) {
  const itemcontext = useContext(StateContext);
  const dispatch = useContext(DispatchContext);
  const navigate = useNavigate();

  const [input,setinput]=useState({
    name:'',
    email:'',
    phone:'',
    addres:'',
    pincode:'',
    payme

  })

  const [name, setname] = useState("");
  const [email, setemail] = useState("");
  const [Phone, setphone] = useState("");
  const [adress, setaddress] = useState("");
  const [pincode, setpicode] = useState("");
  const [payment, setpayment] = useState("");

  let itemForbuy = itemcontext.cartItems.map((item, index) => {
    if (item.id === itemid)
      return (
        <div key={index}>
          <h3>{item.title}</h3>
          <h4>{item.totalprice}</h4>
        </div>
      );
    return null;
  });
  function itemForOrder() {
    itemcontext.cartItems.map((item) => {
      if (item.id === itemid)
        dispatch({
          type: "order-summit",
          payload: {
            id: item.id,
            title: item.title,
            img: item.img,
            qty: item.qty,
            totalprice: item.totalprice,
            person: name,
            address: adress,
            pincode: pincode,
            payment: payment,
            phone: Phone,
          },
        });
      return null;
    });
  }
  function handleSummit(event) {
    event.preventDefault();
    alert("item orderd successfully  !!");
  }
  function navigateorder() {
    navigate("/orders");
  }

  return (
    <section>
      <div className="container">
        <div className="userform flex text-center">
          <div className="userform-content">
            <form>
              <div>
                <label>
                  <h2>Name :</h2>{" "}
                  <input
                    value={name}
                    name='name'
                    onChange={(e) => setname(e.target.value)}
                    type="text"
                    placeholder="Enter Your Name"
                  />
                </label>
              </div>
              <div>
                <label>
                  <h2>Email :</h2>{" "}
                  <input
                    value={email}
                    name='email'
                    onChange={(e) => setemail(e.target.value)}
                    type="email"
                    placeholder="Enter Your Email"
                  />
                </label>
              </div>
              <div>
                <label>
                  {" "}
                  <h2>Phone No : </h2>
                  <input
                    value={Phone}
                    name='phone'
                    onChange={(e) => setphone(e.target.value)}
                    type="tel"
                    placeholder="Enter Your Phone no"
                  />
                </label>
              </div>
              <div>
                <label>
                  <h2>Address : </h2>
                  <input
                    value={adress}
                    name='address'
                    onChange={(e) => setaddress(e.target.value)}
                    type="text"
                    placeholder="Enter Your Adress"
                  />
                </label>
              </div>
              <div>
                <label>
                  {" "}
                  <h2>Pincode : </h2>
                  <input
                    value={pincode}
                    name='pincode'
                    onChange={(e) => setpicode(e.target.value)}
                    type="text"
                    placeholder="Enter Your Pincode"
                  />
                </label>
              </div>

              <div className="userform-content-orders">
                <label>
                  <h2>Your orders:</h2>
                  {itemForbuy}
                </label>
              </div>
              <div>
                <h3>Payment Methode :</h3>
                <label>
                  COD
                  <input
                    onClick={(e) => setpayment(e.target.value)}
                    type="radio"
                    name="cod"
                    value="COD"
                  />
                </label>
                <label>
                  UPI
                  <input
                    onClick={(e) => setpayment(e.target.value)}
                    type="radio"
                    name="cod"
                    value="UPI"
                  />
                </label>
                <label>
                  ATM
                  <input
                    onClick={(e) => setpayment(e.target.value)}
                    type="radio"
                    name="cod"
                    value="ATM"
                  />
                </label>
              </div>

              <div>
                <button
                  onClick={(event) => {
                    handleSummit(event);
                    itemForOrder();
                    navigateorder();
                  }}
                  type="submit"
                >
                  Submit
                </button>
              </div>
            </form>
          </div>
          <h3 onClick={closeuserForm} className="userform-close">
            Close
          </h3>
        </div>
      </div>
    </section>
  );
}

export default UseForm;
